public int searchInsert(int[] nums, int target) {
    int len=nums.length;
    int lo=0,hi=len-1;
    int res=hi;
    while(lo<=hi){
        int mid=lo+(hi-lo)/2;
        if(nums[mid]>=target){
            res=mid;
            hi=mid-1;
        }else {
            lo=mid+1;
        }
    }
    return nums[res]<target?len:res;
}
