    for(int w:weights){
        max=Math.max(w,max);
        sum+=w;
    }
    int left=Math.max(sum/D,max),right=sum;
    while(left<right){ //这里一开始写成<=了....
        int mid=left+(right-left)/2;
        if(check(weights,mid,D)){
            right=mid;
