                      } catch (InterruptedException e) {
                          System.out.println("线程1 wait方法抛出了InterruptedException异常");
                      }

                      try {
                          TimeUnit.SECONDS.sleep(3);
                      } catch (InterruptedException e) {
                          e.printStackTrace();
