              }
          }, "线程1");
          thread1.start();
          // 这里让 thread1 和 thread2 先起来，然后再起后面的 thread3
         try {
              Thread.sleep(1000);
          } catch (InterruptedException e) {
          }

          new Thread(new Runnable() {
              @Override
              public void run() {
